  `cover_image` varchar(1023) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '封面',
  `introduce` varchar(511) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '介绍',
  `reads` int NOT NULL COMMENT '阅读数',
  `likes` int NOT NULL COMMENT '喜欢数',
  `press` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '出版社',
  `category` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '种类',
  PRIMARY KEY (`book_id`) USING BTREE,
  INDEX `author_ibfk_1`(`author_id` ASC) USING BTREE,
  CONSTRAINT `author_ibfk_1` FOREIGN KEY (`author_id`) REFERENCES `author` (`author_id`) ON DELETE RESTRICT ON UPDATE RESTRICT
) ENGINE = InnoDB CHARACTER SET = utf8mb4 COLLATE = utf8mb4_0900_ai_ci ROW_FORMAT = DYNAMIC;

-- ----------------------------
-- Records of book
-- ----------------------------
INSERT INTO `book` VALUES (1, 1, '《庄子》', 'https://gimg2.baidu.com/image_search/src=http%3A%2F%2Fupload.cnpubg.com%2F2018%2F0108%2F1515370642224.jpg&refer=http%3A%2F%2Fupload.cnpubg.com&app=2002&size=f9999,10000&q=a80&n=0&g=0n&fmt=auto?sec=1673364460&t=fd46eb1ba0a8b736c1232b9b4422b1eb', '《庄子》又名《南华经》，是战国中后期庄子及其后学所著道家学说汇总。《庄子》对工具理性也进行了深刻批判。《庄子》进一步提出了“得意忘言”的观点。到了汉代以后，尊庄子为南华真人，因此《庄子》亦称《南华经》。其书与《老子》《周易》合称“三玄”。《庄子》一书主要反映了庄子的批判哲学、艺术、美学、审美观等。其内容丰富，博大精深，涉及哲学、人生、政治、社会、艺术、宇宙生成论等诸多方面。', 0, 0, '中华书局', NULL);
INSERT INTO `book` VALUES (2, 2, '《道德经》', 'https://gimg2.baidu.com/image_search/src=http%3A%2F%2Fimages-cn.ssl-images-amazon.cn%2Fimages%2FI%2F41p6RaQYvCS.jpg&refer=http%3A%2F%2Fimages-cn.ssl-images-amazon.cn&app=2002&size=f9999,10000&q=a80&n=0&g=0n&fmt=auto?sec=1673364486&t=e7a77e2f62c12cf9160c6ab68123fdab', '\r\n《道德经》，春秋时期老子所著的哲学作品，又称《道德真经》《老子》《五千言》《老子五千文》，是中国古代先秦诸子分家前的一部著作，是道家哲学思想的重要来源。道德经分上下两篇，原文上篇《德经》、下篇《道经》，不分章，后改为《道经》37章在前，第38章之后为《德经》，并分为81章。', 0, 0, '中华书局', NULL);
INSERT INTO `book` VALUES (3, 3, '《世说新语》', 'https://img0.baidu.com/it/u=3751800269,1213290172&fm=253&fmt=auto&app=138&f=JPEG?w=482&h=750', '《世说新语》是南朝宋文学家刘义庆撰写（一说刘义庆组织门客编写）的文言志人小说集，是魏晋轶事小说的集大成之作，是魏晋南北朝时期“笔记小说”的代表作。其内容主要是记载东汉后期到魏晋间一些名士的言行与轶事。通行本6卷36篇。有梁刘孝标注本。', 0, 0, '中华书局', NULL);
INSERT INTO `book` VALUES (4, 4, '《孟子》', 'https://gimg2.baidu.com/image_search/src=http%3A%2F%2Fimg12.360buyimg.com%2Fn1%2Fjfs%2Ft3331%2F157%2F73212178%2F124731%2Feaaa68f6%2F57fdfcc0N7331fb4d.jpg&refer=http%3A%2F%2Fimg12.360buyimg.com&app=2002&size=f9999,10000&q=a80&n=0&g=0n&fmt=auto?sec=1673499560&t=102115e360b5ae3c63089951f70ba175', '《孟子》是儒家的经典著作，战国中期孟子及其弟子万章、公孙丑等著。最早见于赵岐《孟子题辞》：“此书，孟子之所作也，故总谓之《孟子》”。《孟子》被南宋朱熹列为“四书”（另外三本为《大学》《中庸》《论语》）。\r\n《汉书·艺文志》著录《孟子》十一篇，现存七篇十四卷。总字数三万五千余字，260章。相传另有《孟子外书》四篇，已佚（今本《孟子外书》系明姚士粦伪作）。书中记载有孟子及其弟子的政治、教育、哲学、伦理等思想观点和政治活动。古代考试主要考“四书”与“五经”。\r\n《孟子》作为《中华优秀传统文化百部经典》之一，2021年已经推出。', 0, 0, '中华书局', '');
INSERT INTO `book` VALUES (5, 5, '《陶庵梦忆》', 'http://t14.baidu.com/it/u=1254146011,3318722176&fm=224&app=112&f=JPEG?w=339&h=500', '《陶庵梦忆》，明代散文集。为明末清初散文家张岱所著。该书共八卷，成书于甲申明亡（1644年）之后，直至乾隆四十年（1775年）才初版行世。其中所记大多是作者亲身经历过的杂事，将种种世相展现在人们面前。', 0, 0, '中华书局', NULL);
INSERT INTO `book` VALUES (6, 6, '《东京梦华录》', 'https://img14.360buyimg.com/pop/jfs/t1/124010/28/8239/216047/5f23bebeE7f75d622/7a16a6d3bde1ad8f.jpg', '《东京梦华录》是宋代孟元老的笔记体散记文，创作于宋钦宗靖康二年（公元1127年）。\r\n该著作追述北宋都城东京开封府城市风俗人情，所记大多是宋徽宗崇宁到宣和（公元1102年-1125年）年间北宋都城东京开封的情况，描绘了这一历史时期居住在东京的上至王公贵族、下及庶民百姓的日常生活情景，是研究北宋都市社会生活、经济文化的一部重要的历史文献古籍。', 0, 0, '中华书局', NULL);
INSERT INTO `book` VALUES (7, 7, '《阅微草堂笔记》', 'https://img1.baidu.com/it/u=4012090687,1992606284&fm=253&fmt=auto&app=138&f=JPEG?w=500&h=630', '《阅微草堂笔记》原名《阅微笔记》，是清朝翰林院庶吉士出身的纪昀于乾隆五十四年（公元1789年）至嘉庆三年（公元1798年）间以笔记形式所编写成的文言短篇志怪小说。\r\n在时间上，《阅微草堂笔记》主要搜辑各种狐鬼神仙、因果报应、劝善惩恶等当时代前后的流传的乡野怪谈，或亲身所听闻的奇情轶事；在空间地域上，其涵盖的范围则遍及全中国，远至乌鲁木齐、伊宁、滇黔等地。\r\n同时《阅微草堂笔记》有意模仿宋代笔记小说质朴简淡的文风，曾在历史上一时享有同《红楼梦》、《聊斋志异》并行海内的盛誉。崇文书局有限公司2018年7月出版的《阅微草堂笔记》入选2019年全国教师暑期阅读推荐书目第二批。', 0, 0, '中华书局', NULL);
INSERT INTO `book` VALUES (8, 8, '《徐霞客游记》', 'https://img14.360buyimg.com/pop/jfs/t1/192074/36/769/18310/608c06c1Ed4f49818/38b474cd090ec30d.jpg', '《徐霞客游记》是明代地理学家徐霞客创作的一部散文游记，明末徐弘祖经34年旅行，写有天台山、雁荡山、黄山、庐山等名山游记17篇和《浙游日记》、《江右游日记》、《楚游日记》、《粤西游日记》、《黔游日记》、《滇游日记》等著作，除佚散者外，遗有60余万字游记资料。死后由他人整理成《徐霞客游记》。世传本有10卷、12卷、20卷等数种。在原稿的基础之上，王忠纫手校、季梦良续成稿本，成书于崇祯十五年（1642年）。\r\n《徐霞客游记》主要按日记述作者1613年至1639年间旅行观察所得，对地理、水文、地质、植物等现象，均做了详细记录。\r\n《徐霞客游记》是系统考察中国地貌地质的开山之作，同时也描绘了中国大好河山的风景资源，此外优美的文字也使之成为文学佳作，在地理学和文学上都有着重要的价值。', 0, 0, '中华书局', NULL);

