-- ----------------------------
-- Table structure for daily_sentence
-- ----------------------------
DROP TABLE IF EXISTS `daily_sentence`;
CREATE TABLE `daily_sentence`  (
  `author` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '作者',
  `content` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '诗句',
  `time` date NULL DEFAULT NULL COMMENT '日期',
  `source` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '诗句出处',
  `id` int NOT NULL AUTO_INCREMENT,
  `image_url` varchar(1023) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT '',
  `create_time` datetime NULL DEFAULT CURRENT_TIMESTAMP,
  `update_time` datetime NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 101 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_0900_ai_ci ROW_FORMAT = DYNAMIC;

-- ----------------------------
-- Records of daily_sentence
-- ----------------------------
INSERT INTO `daily_sentence` VALUES ('（元）王德信', '今春，香肌瘦几分，缕带宽三寸。', '2022-12-16', '《中吕·十二月过尧民歌》', 1, 'https://so1.360tres.com/t016926afa710f0dbca.jpg', '2022-12-10 12:09:40', '2022-12-14 07:34:10');
INSERT INTO `daily_sentence` VALUES ('（唐）贯休', '满堂花醉三千客，一剑霜寒十四州。', '2022-12-15', '《献钱尚父》', 2, 'https://www.mwshe.cn/wp-content/uploads/2020/12/46f0a7754db24405bada5524ba5ac459.jpg', '2022-12-10 12:09:40', '2022-12-14 07:35:43');
INSERT INTO `daily_sentence` VALUES ('（战国）庄子', '天地一指也，万物一马也。', '2022-12-14', '《齐物论》', 3, 'https://picx.zhimg.com/v2-e8bb1db13b88bdf32d690db1baf3cf40_1440w.jpg?source=172ae18b', '2022-12-10 12:57:11', '2022-12-14 08:33:00');
INSERT INTO `daily_sentence` VALUES ('（北宋）苏轼', '寄蜉蝣于天地，渺沧海之一粟。', '2022-12-13', '《赤壁赋》', 4, 'https://tse4-mm.cn.bing.net/th/id/OIP-C.QkPP_kau45mRGIsQiVPq6AHaSf?pid=ImgDet&rs=1', '2022-12-10 13:05:53', '2022-12-14 07:43:43');
INSERT INTO `daily_sentence` VALUES ('（北宋）王安石', '而世之奇伟、瑰怪，非常之观，常在于险远，而人之所罕至焉，故非有志者不能至也。', '2022-12-12', '《游褒禅山记》', 5, 'https://ts1.cn.mm.bing.net/th/id/R-C.d489df135d6448eef4defa8b6a3b4cb8?rik=PDK0SwWAJiTxFg&riu=http%3a%2f%2fp5.qhmsg.com%2ft017bfccfe02f8e0cf7.jpg&ehk=Vdn7rWTE%2biuKVleRYR7y5RCmlfoMwJOQRhG3mM1DQPQ%3d&risl=&pid=ImgRaw&r=0', '2022-12-10 13:05:53', '2022-12-14 08:29:02');
INSERT INTO `daily_sentence` VALUES ('（东晋）袁宏', '潜鱼择渊，高鸟候柯。', '2022-12-11', '《三国名臣序赞》', 6, 'https://www.dasouji.com/wp-content/uploads/2018/07/%E7%BF%A0%E9%B8%9F-12.jpg', '2022-12-10 13:05:53', '2022-12-14 08:29:02');
INSERT INTO `daily_sentence` VALUES ('（三国·魏）曹植', '其形也，翩若惊鸿，婉若游龙。', '2022-12-10', '《洛神赋》', 7, 'https://p1.ssl.qhimg.com/t0167e0f25f565fbe4c.jpg', '2022-12-10 13:16:24', '2022-12-14 08:29:01');
INSERT INTO `daily_sentence` VALUES ('（宋）仲殊', '十里青山远，潮平路带沙。', '2022-12-09', '《南柯子·忆旧》', 8, 'https://img.zcool.cn/community/0183f65c6a854ba801203d224e8fdf.jpg?x-oss-process=image/auto-orient,1/resize,m_lfit,w_1280,limit_1/sharpen,100/format,webp/quality,q_100', '2022-12-10 13:16:24', '2022-12-14 07:56:08');
INSERT INTO `daily_sentence` VALUES ('（宋）柳永', '念去去，千里烟波，暮霭沉沉楚天阔。', '2022-12-08', '《雨霖铃·寒蝉凄切》', 9, 'https://picx.zhimg.com/v2-6cff003566fc7acd6738630d3d714ed7_1440w.jpg?source=172ae18b', '2022-12-10 13:16:24', '2022-12-14 07:54:09');
INSERT INTO `daily_sentence` VALUES ('（宋）李清照', '物是人非事事休，欲语泪先流。', '2022-12-07', '《武陵春》', 10, 'https://ts1.cn.mm.bing.net/th/id/R-C.fd11be175338c4df44b4edced5aed8bc?rik=4kU00e8JLOdj1A&riu=http%3a%2f%2fi0.qhimg.com%2ft0142973da2991d6e59.jpg&ehk=T9o9nw0Xvt4pwIcvJXJfheA%2f%2bxrRj980NQnj7CQPgzs%3d&risl=&pid=ImgRaw&r=0', '2022-12-10 13:16:24', '2022-12-14 07:37:57');
INSERT INTO `daily_sentence` VALUES ('（北宋）张载', '为天地立心，为生民立命，为往圣继绝学，为万世开太平。', '2022-12-06', '《横渠语录》', 11, 'https://static.gushixuexi.com/wp-content/uploads/2016/09/09-78.jpg', '2022-12-13 03:40:04', '2022-12-14 08:21:04');
INSERT INTO `daily_sentence` VALUES ('（清）赵执信', '君看落空阔，何异大星芒。', '2022-12-05', '《萤火》', 12, 'https://img.zcool.cn/community/0133bb59659984a8012193a316c5b2.jpg@1280w_1l_2o_100sh.jpg', '2022-12-10 13:16:25', '2022-12-14 07:41:13');
INSERT INTO `daily_sentence` VALUES ('（唐）温庭筠', '词客有灵应识我，霸才无主始怜君。', '2022-12-04', '《过陈琳墓》', 13, 'https://tse4-mm.cn.bing.net/th/id/OIP-C.RWmpxqAgoR--Nja6NbTGZwHaEc?pid=ImgDet&rs=1', '2022-12-10 13:16:25', '2022-12-14 07:42:49');
INSERT INTO `daily_sentence` VALUES ('（周）释迦牟尼', '过去心不可得，现在心不可得，未来心不可得。', '2022-12-03', '《金刚经》', 14, 'https://tse1-mm.cn.bing.net/th/id/OIP-C.35X_b-WJPzdheCM45hi6MAHaE8?pid=ImgDet&rs=1', '2022-12-10 13:16:25', '2022-12-14 07:49:33');
INSERT INTO `daily_sentence` VALUES ('（唐）李白', '应是天仙狂醉，乱把白云揉碎。', '2022-12-02', '《清平乐·画堂晨起》', 15, 'https://ts1.cn.mm.bing.net/th/id/R-C.017f514af4e8f2eb2dd4fd329c43842b?rik=OVj8ySCj1ABIRQ&riu=http%3a%2f%2fi1.qhmsg.com%2ft0199e96f3f31b60204.jpg&ehk=IqHoGBguo89liFtYdvBoK74X4iLmvboc1%2bX24QLrrJs%3d&risl=&pid=ImgRaw&r=0&sres=1&sresct=1', '2022-12-10 13:16:25', '2022-12-14 08:00:23');
