INSERT INTO `daily_sentence` VALUES ('（唐）白居易', '独坐黄昏谁是伴，紫薇花对紫微郎⑷。', '2022-11-25', '《紫薇花》', 22, 'https://img.huabaike.com/tukuimgs/78/20200911144837_663248.jpg', '2022-12-10 13:16:25', '2022-12-14 08:21:30');
