INSERT INTO `daily_sentence` VALUES ('（宋）辛弃疾', '头上貂蝉贵客，花外麒麟高冢，人世竟谁雄？', '2022-11-21', '《水调歌头·我饮不须劝》', 26, 'https://ts1.cn.mm.bing.net/th/id/R-C.f51adfe1659b917322f94f0938f621da?rik=79Z7%2ft1%2fwQmrzA&riu=http%3a%2f%2fx0.ifengimg.com%2fres%2f2019%2f4603029B885BF29097E860D242CC14CE2F62460F_size61_w640_h360.jpeg&ehk=z8KUEaT4eDEXfVCQcSthx%2b%2fBPG2w75jzCRxP5Fzpbgs%3d&risl=&pid=ImgRaw&r=0&sres=1&sresct=1', '2022-12-10 13:16:25', '2022-12-14 08:03:29');
INSERT INTO `daily_sentence` VALUES ('（宋）范成大', '晚晴风歇，一夜春威折。', '2022-12-17', '《霜天晓角·梅》', 27, 'https://ts1.cn.mm.bing.net/th/id/R-C.37ca8bc8460d172b0f0ec00985702c35?rik=6ua5EjxnO1n9tw&riu=http%3a%2f%2fwww.exam58.com%2fuploads%2fallimg%2f130704%2f1-130F4224S22W.jpg&ehk=0vnUXW8iOdejWzsztK44eQj%2ftF5Z8F3BvHPVBpQfHxg%3d&risl=&pid=ImgRaw&r=0', '2022-12-10 13:16:25', '2022-12-14 08:18:43');
