-- ----------------------------
-- Table structure for word
-- ----------------------------
DROP TABLE IF EXISTS `word`;
CREATE TABLE `word`  (
  `word_id` int NOT NULL AUTO_INCREMENT,
  `book_id` int NOT NULL COMMENT '诗句出处的书本id',
  `chapter_id` int NOT NULL COMMENT '字词出处的章节id',
  `content` varchar(511) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '诗句',
  `answer_a` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '选项a',
  `answer_b` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '选项b',
  `answer_c` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '选项c',
  `answer_d` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '选项d',
  `correct_answer` int NOT NULL COMMENT '正确答案',
  `key_word` varchar(63) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '实词',
  `key_index` int NOT NULL COMMENT '实词索引',
  `translate` varchar(1023) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '翻译',
  PRIMARY KEY (`word_id`, `book_id`, `chapter_id`) USING BTREE,
  INDEX `word_id`(`word_id` ASC) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 28 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_0900_ai_ci ROW_FORMAT = DYNAMIC;

-- ----------------------------
-- Records of word
-- ----------------------------
INSERT INTO `word` VALUES (1, 1, 1, '北冥有鱼，其名为鲲。', '北方的天空。', '北海。冥，通“溟”，浩瀚无边。', '昏暗的样子。', '古代神话的神明之一。', 1, '北冥', 0, '北海有一条鱼，它的名字叫做鲲。');
INSERT INTO `word` VALUES (2, 1, 1, '怒而飞，其翼若垂天之云。', '下垂，垂挂。', '流传，传颂。', '通“陲”，边陲，边际。', '临近，接近。', 2, '垂', 0, '奋起而飞，它的翅膀就像天边的云。');
INSERT INTO `word` VALUES (3, 1, 1, '野马也，尘埃也，生物之以息相吹也。', '呼吸；气息；风。', '生长，增长。', '停止；灭；休息。', '利息。', 0, '息', 0, '野马般的气雾，飞扬的浮尘，这都是生物的气息相互吹拂的结果。');
INSERT INTO `word` VALUES (4, 1, 1, '适莽苍者，三餐而反，腹犹果然。', '女子出嫁。', '适合，适宜；顺从，满足。', '刚才，刚刚。', '到……去。', 3, '适', 0, '到郊野去，只需携带三顿饭食。');
INSERT INTO `word` VALUES (5, 1, 1, '小知不及大知，小年不及大年。', '通“智”。聪明，智慧。', '知道；知觉。', '见解；见识。', '了解，赏识。', 0, '知', 0, '智慧小的不如智慧大的，寿命短的不如寿命长的。');
INSERT INTO `word` VALUES (6, 1, 1, '我腾跃而上，不过数仞而下，翱翔蓬蒿之间，此亦飞之至也！', '至于。', '招引，引来。', '极，最。', '到，到达。', 2, '至', 0, '我腾跃而起，飞不过几丈高就落下来，在蓬蒿丛中飞来飞去，这也是飞翔中很极致的境界了！');
INSERT INTO `word` VALUES (7, 1, 1, '若夫乘天地之正，而御六气之辩，以游无穷者，彼且恶乎待哉！', '驾车；乘船。', '压服，欺压。', '追逐。', '因循，凭借。', 3, '乘', 0, '如果能够把握天地的本性，顺应六气的变化，畅游于无穷的世界，他还有什么必须依赖的东西呢！');
INSERT INTO `word` VALUES (8, 1, 1, '夫子立而天下治，而我犹尸之，吾自视缺然。', '尸体；收尸；陈列尸体。', '古代祭祀时代表死者受祭的活人。', '主管；主持。', '布阵。', 2, '尸', 0, '倘若您登上大位，天下就会安定，而我还在占着您的位子，自己感到太不够格了。');
INSERT INTO `word` VALUES (9, 1, 1, '吾以是狂而不信也。', '狗发疯。', '通“诳”，谎言。', '放荡，不受约束。', '气势猛烈。', 1, '狂', 0, '我因此认为纯属诳言而根本不信。');
INSERT INTO `word` VALUES (10, 1, 1, '宋人资章甫而适诸越，越人断发文身，无所用之。', '贩卖。', '资质。', '凭借。', '积蓄。', 0, '资', 0, '宋国人到越国去贩卖礼帽，越国人习惯剪掉头发，身刺花纹，根本用不上它。');
INSERT INTO `word` VALUES (11, 1, 2, '子游曰：“敢问其方。”', '方；正直。', '古代称面积的用语。', '方向，方位；区域，地方。', '方法，办法。', 3, '方', 0, '子游说：“请问其中的方法。”');
INSERT INTO `word` VALUES (12, 1, 2, '前者唱于而随者唱喁，泠风则小和，飘风则大和，厉风济则众窍为虚。', '过河，渡。', '停止。', '帮助，接济；有利，有益。', '成。', 1, '济', 0, '前面的风呜呜地唱着，后面的风就呼呼地和着，微风就小声地应和着，大风就大声地应和着，当暴风过后，所有地窍穴就都虚寂无声了。');
INSERT INTO `word` VALUES (13, 1, 2, '其发若机括，其司是非之谓也。', '主管，掌管。', '通“伺”。侦察，探察。', '观察。', '官署。', 1, '司', 0, '他们有时发言就像放出的利箭，探察到别人的是非来进行攻击。');
INSERT INTO `word` VALUES (14, 1, 2, '其杀若秋冬，以言其日消也。', '杀死，杀戮。', '灭除，败坏。', '衰微，衰败。', '草木枯萎。', 2, '杀', 0, '他们犹如秋冬的萧条般衰竭，这是说他们一天天地走向消亡。');
INSERT INTO `word` VALUES (15, 1, 2, '终身役役而不见其成功，苶然疲役而不知其所归。', '疲倦的样子。', '伤痛的样子。', '冷漠的样子。', '兴致缺缺的样子。', 0, '苶然', 0, '终身奔忙劳碌却不见成功，疲惫困顿却不知前途，这不是很可悲吗？');
INSERT INTO `word` VALUES (16, 1, 2, '人之生也，固若是芒乎？', '通“茫”。模糊不清；昏昧无知。', '一种茅草。可制作绳索和草鞋。', '刀枪的锋芒，刀尖。', '光芒。', 0, '芒', 0, '人的一生，本来就如此昏昧吗？');
INSERT INTO `word` VALUES (17, 1, 2, '欲是其所非而非其所是，则莫若以明。', '明亮；白天；照亮。', '证明；说明。', '视力，视力好。', '用洞彻之心去观照事物，以明于大道。', 3, '明', 0, '如果肯定对方所否定的而否定对方所肯定的，则不如以空明的心境去观照事物的本源。');
INSERT INTO `word` VALUES (18, 1, 2, '彼是莫得其偶，谓之道枢。', '偶像，木偶。', '双，成双；对仗，对偶。', '相对，对面，对立', '碰巧，偶然。', 2, '偶', 0, '如果超脱了彼与此、是与非的对立关系，就叫掌握了大道的枢要。');
INSERT INTO `word` VALUES (19, 1, 2, '故为是举莛与楹，厉与西施，恢诡谲怪，道通为一。', '磨刀石；磨；磨炼。', '勉励，激励。', '癞疮', '剧烈，猛。', 2, '厉', 0, '所以就像草茎与房柱、丑女与西施，以及世上诸如恢诡谲怪的种种奇异现象，从大道的观点来看，都是浑然一体的。');
INSERT INTO `word` VALUES (20, 1, 2, '是以圣人和之以是非，而休乎天钧，是之谓两行。', '古代重量单位，三十斤为一钧。', '通“均”。平均。', '制作陶器所用的转轮。', '敬辞，用于有关对方的事物或行为。', 1, '钧', 0, '所以圣人混同于是是非非，而任凭自然均衡，这就是物我并行，各得其所。');
INSERT INTO `word` VALUES (21, 7, 1, '鬼神见之犹敛避，况我曹乎！', '辈。', '对，双；群。', '分科办事的官署或部门。', '周代诸侯国，在今山东定陶。', 0, '曹', 0, '鬼神见到她尚且要敛迹退避，何况是我们这样的呢！');
INSERT INTO `word` VALUES (22, 7, 1, '下舟行数十步，翳然灭迹。', '用羽毛做的舞具。', '遮蔽，掩盖，隐藏。', '眼睛上长的膜。', '阴影。', 1, '翳', 0, '下船走了几十步，一下子隐灭不见了。');
INSERT INTO `word` VALUES (23, 7, 1, '然利己者必损人，种种机械，因是而生，种种冤愆。', '差错，违背。', '失去，丧失。', '延误，超过。', '罪过，过失。', 3, '愆', 0, '想要利己的人必定会损害别人，种种诡诈奸巧就因此发生，种种诬陷冤屈事件也因此制造出来。');
INSERT INTO `word` VALUES (24, 7, 1, '额急挥骑驰及，遽格道士手，女噭然一声，化火光飞去。', '送信的快马或快车。', '迅速，急速；突然，仓促。', '就，竟。', '恐惧。', 2, '遽', 0, '额都统急忙打马奔过去，突然格开道士的手，美女子“噭”地一声，化成一道火光飞去。');
INSERT INTO `word` VALUES (25, 7, 1, '又染恶疮，医药备至，比愈，则已荡然矣。', '并列，挨着。', '接连；处处，到处。', '及，等到。', '亲近；勾结。', 2, '比', 0, '染上了恶疮，看病吃药破费许多，等到病疮痊愈，所有积蓄荡然无存。');
INSERT INTO `word` VALUES (26, 7, 2, '喟然曰：“甚哉，治狱之可畏也！此幸不误，安保他日之不误耶？”', '叹息。', '羞愧，惭愧。', '糊涂无知。', '溃烂。', 0, '喟', 0, '他长叹了口气说：“真是不得了，审理案件真可怕啊！这次幸运不出错，怎么能够保证以后的日子不出错呢？”');
INSERT INTO `word` VALUES (27, 7, 2, '诣墓所，且数且詈。', '欺压，欺凌。', '骂；责备。', '忧患，苦难。', '遭遇。', 1, '詈', 0, '他到了墓地，一边数落一边骂。');

-- ----------------------------
-- Table structure for word_collection
-- ----------------------------
DROP TABLE IF EXISTS `word_collection`;
CREATE TABLE `word_collection`  (
  `word_id` int NOT NULL COMMENT '题目id',
  `user_id` int NOT NULL COMMENT '用户id',
  PRIMARY KEY (`word_id`, `user_id`) USING BTREE,
  INDEX `word_ibfk_2`(`user_id` ASC) USING BTREE,
  CONSTRAINT `word_ibfk_1` FOREIGN KEY (`word_id`) REFERENCES `word` (`word_id`) ON DELETE RESTRICT ON UPDATE RESTRICT,
  CONSTRAINT `word_ibfk_2` FOREIGN KEY (`user_id`) REFERENCES `user` (`user_id`) ON DELETE RESTRICT ON UPDATE RESTRICT
) ENGINE = InnoDB CHARACTER SET = utf8mb4 COLLATE = utf8mb4_0900_ai_ci ROW_FORMAT = DYNAMIC;

-- ----------------------------
-- Records of word_collection
-- ----------------------------

SET FOREIGN_KEY_CHECKS = 1;
